INSERT INTO `wp_posts` VALUES (194,1,'2023-11-29 04:33:35','2023-11-29 04:33:35','<!-- wp:image {\"id\":116,\"sizeSlug\":\"large\",\"linkDestination\":\"none\",\"className\":\"main-image\"} -->\n<figure class=\"wp-block-image size-large main-image\"><img src=\"http://lgl-website.local/wp-content/uploads/2023/11/img12-1-1024x467.jpg\" alt=\"\" class=\"wp-image-116\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"className\":\"product-description\"} -->\n<p class=\"product-description\">A hyperspectral camera is an instrument for the spectroscopy of 2D objects.<br>Each pixel of the 2D image has spectra in the wavelength range of 400nm - 950nm at a resolution of 2nm.<br>It is a remote sensing instrument that measures the two-dimensional spectra of an object.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Optics:</strong><br>Precise optical component with very good transmission and coatings make the instrument very sensitive and also has very sensetive also has very low stray light.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":117,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"http://lgl-website.local/wp-content/uploads/2023/11/img4-1024x651.jpg\" alt=\"\" class=\"wp-image-117\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p><strong>Interface:</strong><br><a href=\"https://www.hyspim.com/index.php\">HYSPIM</a> cameras has a very usefull gadjet, Interface (mechanical-connector) that conects camera to <a href=\"https://www.hyspim.com/desktop.php\">Desktop scanner</a> , <a href=\"https://www.hyspim.com/field.php\">Field scanner</a> and also common tripod.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":118,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"http://lgl-website.local/wp-content/uploads/2023/11/img3-1024x683.jpg\" alt=\"\" class=\"wp-image-118\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p><strong>Mechanical:</strong><br>It is lightweight and has a small form factor that makes it simple to installed on a drone.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":119,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"http://lgl-website.local/wp-content/uploads/2023/11/img5-1024x771.jpg\" alt=\"\" class=\"wp-image-119\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p><strong>Detector:</strong><br><a href=\"https://www.hyspim.com/index.php\">HYSPIM</a> has very good dynamic range due to using a Basler camera with Pixel Bit Depth of 12bit . also it has very low smearing and blooming.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":120,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://lgl-website.local/wp-content/uploads/2023/11/img14.jpg\" alt=\"\" class=\"wp-image-120\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p><strong>Output:</strong><br><a href=\"https://www.hyspim.com/index.php\">HYSPIM</a> has a USB3 port for spectral outout and USB2 for Field viewer camera (Viewfinder). Field viewer camera is a very useful accessory.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":121,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"http://lgl-website.local/wp-content/uploads/2023/11/img4-1-1024x651.jpg\" alt=\"\" class=\"wp-image-121\"/></figure>\n<!-- /wp:image -->','HYSPIM ALPHA','','inherit','closed','closed','','114-revision-v1','','','2023-11-29 04:33:35','2023-11-29 04:33:35','',114,'http://lgl-website.local/?p=194',0,'revision','',0);
